const css = {};
let check= "";
if (check>1 && check<12 ) {
	check = "good morning";
	css.color = "green";
}
else if(check>=12 && check<= 5){
	check = "aftrnoon";
	css.color = "red";
}
else{
	check = "good night";
	css.color = "blue";
}
return(<> <div> <h1>hello sir, <span style={css}>{check}</span></h1> </div>
 </>);
}

export default App;
