import React, { useState } from 'react';
const App = () => {
    const [fname, setname] = useState({
        fname: '',
        lname: '',
        email: '',
        number: ''
    });
    // const [fn, se] = useState();

    let event = (e) => {
        // console.log(e.target.value);
         let {value,name} = e.target;
        setname((preValue) => {
            return{
           ...preValue,
           [name] :value,
           }
        
        })

    }

    let click = (e) => {
        e.preventDefault();
    alert("submit");



    }


    return ( < >

        <h1 style = {
            { color: 'red', textAlign: 'center' }
        } > Hello, { fname.fname } { fname.lname }  { fname.email } { fname.number } < /h1>
        
         <form onSubmit={click}> 
        
         <
        div > <
        input type = "text"
        name = "name"
        placeholder = "enter name"
        onChange = { event }
        value = { fname.fname }
        / >  <
        br / >
        <
        input type = "text"
        name = "ln"
        placeholder = "enter lname"
        onChange = { event }
        value = { fname.lname }
        / >  <
        br / >
        
        <
        input type = "email"
        name = "email"
        placeholder = "enter email"
        onChange = { event }
        value = { fname.email }
        / >  <
        br / >
        

        <
        input type = "text"
        name = "number"
        placeholder = "enter name"
        onChange = { event }
        value = { fname.number }
        / >  <
        br / >

         <
        button type = "submit" > CLick here < /button>  < /div >
        <
        /form> < / >
    )
}
export default App;
