import React, { useState } from 'react';
const App = () => {
    const [fname, setname] = useState({
        fname: '',
        lname: ''
    });
    const [fn, se] = useState();

    let event = (e) => {
        // console.log(e.target.value);
        setname((preValue) => {
            let value = e.target.value;
            let name = e.target.name;
            if (name === 'name') {
                return {
                    fname: value,
                    lname: preValue.lname
                }


            } else if (name === 'ln') {
                return {
                    fname: preValue.fname,
                    lname: value
                }


            }
        })

    }

    let click = (e) => {
        e.preventDefault();
        se(fname.fname);



    }


    return ( < >

        <
        h1 style = {
            { color: 'red', textAlign: 'center' }
        } > Hello, { fn } { fname.lname } < /h1> <form onSubmit={click}>  <
        div > <
        input type = "text"
        name = "name"
        placeholder = "enter name"
        onChange = { event }
        value = { fname.fname }
        / >  <
        br / >
        <
        input type = "text"
        name = "ln"
        placeholder = "enter lname"
        onChange = { event }
        value = { fname.lname }
        / >  <
        br / > <
        button type = "submit" > CLick here < /button>  < /div >
        <
        /form> < / >
    )
}
export default App;
