import React,{useState} from 'react';
import './card.css';
import {Info} from './Info.jsx'

const Card = (props) => {
  const [open, setIsOpen] = useState(false);

 // const openForm = () => setIsOpen(true);

  redirectHandler = () => {
    setIsOpen(true);
    renderRedirect();
}
renderRedirect = () => {
    if (open) {
        return <Redirect to='/show-detail' />
    }
}

return(
    <>
    
     <div className="card check ">
          <img src={props.img} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{props.title} </h5>
             <button className="btn btn-primary" onClick={redirectHandler} >More Detail</button>
             {open === true ? <Info key={props.key} summary={props.summary} open={open}/> : null }
            
            
          </div>
        </div>
        
    </>
)
}
export default Card;
