import React,{useState,createContext} from 'react';
import './card.css';
import {Info} from './Info.jsx'
import {Link  } from 'react-router-dom';

const First = createContext();
const Card = (props) => {
  const [isOpen, setIsOpen] = useState(false);
 
  const togglePopup = () => {
    setIsOpen(true);
  }

  // console.log(props.data);

return(
    <>
   
     <div className="card check ">
          <img src={props.img} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{props.title} </h5>
             <button className="btn btn-primary" onClick={togglePopup} >
               <Link  className="btn btn-primary" to={{ pathname: `/detail/${props.data.show.id}`}}>
               More Detail </Link>
                </button> 
             {isOpen === true ? <First.Provider value = {props.data}> <Info  data={props.data} /> </First.Provider>  : null }
             
            
            
          </div>
        </div>
        
    </>
)
}
export default Card;
