  import React, { useState } from 'react';
  const App = () => {
      let newT = new Date().toLocaleTimeString();
      let [time, ftime] = useState(newT);

      const update = () => {
          newT = new Date().toLocaleTimeString();
          ftime(newT);
      }
      return ( <
          >
          <
          h1 > TIME { time } < /h1> <
          button onClick = { update } > CLICK HERE < /button> < / >
      )
  }
  export default App;
