import React, { useState } from 'react';
const App = () => {
    const [name, setname] = useState("");
    const [fname, fsetname] = useState("");
    // last name
    const [lname, lsetname] = useState("");
    const [llname, llsetname] = useState("");

    let event = (e) => {
            // console.log(e.target.value);
            setname(e.target.value);

        }
        // let events = (e) => {
        //     // console.log(e.target.value);

    //     lsetname(e.target.value);
    // }
    let click = (e) => {
        e.preventDefault();
        fsetname(name)
        llsetname(lname);
    }


    return ( < >

        <
        h1 style = {
            { color: 'red', textAlign: 'center' }
        } > Hello, { fname } { llname } < /h1> <form onSubmit={click}>  <
        div > <
        input type = "text"
        placeholder = "enter name"
        onChange = { event }
        value = { name }
        / >  <
        br / >
        <
        input type = "text"
        placeholder = "enter lname"
        onChange = { events }
        value = { lname }
        / >  <
        br / > <
        button type = "submit" > CLick here < /button>  < /div >
        <
        /form> < / >
    )
}
export default App;
