import React, { useState } from 'react';
const App = () => {
    const [fname, setname] = useState({
        fname: '',
        lname: '',
        email: '',
        number: ''
    });
    // const [fn, se] = useState();

    let event = (e) => {
        // console.log(e.target.value);
        setname((preValue) => {
            let value = e.target.value;
            let name = e.target.name;
            if (name === 'name') {
                return {
                    fname: value,
                    lname: preValue.lname
                }


            } else if (name === 'ln') {
                return {
                    fname: preValue.fname,
                    lname: value,
                    email:preValue.email,
                    number:preValue.number
                }


            }
          else if (name === 'email') {
                return {
                    fname: preValue.fname,
                    lname: preValue.lname,
                    email:value,
                    number:preValue.number
                }


            }


                else if (name === 'ln') {
                return {
                   fname: preValue.fname,
                    lname: preValue.lname,
                    email:preValue.email,
                    number:value
                }


            }

        })

    }

    let click = (e) => {
        e.preventDefault();
        // se(fname.fname);



    }


    return ( < >

        <
        h1 style = {
            { color: 'red', textAlign: 'center' }
        } > Hello, { fname.fname } { fname.lname } < /h1> <form onSubmit={click}>  <
        div > <
        input type = "text"
        name = "name"
        placeholder = "enter name"
        onChange = { event }
        value = { fname.fname }
        / >  <
        br / >
        <
        input type = "text"
        name = "ln"
        placeholder = "enter lname"
        onChange = { event }
        value = { fname.lname }
        / >  <
        br / >
        
        <
        input type = "email"
        name = "email"
        placeholder = "enter email"
        onChange = { event }
        value = { fname.email }
        / >  <
        br / >
        

        <
        input type = "number"
        name = "number"
        placeholder = "enter name"
        onChange = { event }
        value = { fname.number }
        / >  <
        br / >

         <
        button type = "submit" > CLick here < /button>  < /div >
        <
        /form> < / >
    )
}
export default App;
